package com.javaex.service;

import java.io.BufferedOutputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;
import java.util.List;
import java.util.UUID;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.web.multipart.MultipartFile;

import com.javaex.dao.FileUploadDAO;
import com.javaex.vo.FileVO;

@Service
public class FileUploadService {
	
	
	@Autowired
	private FileUploadDAO dao;

	public void restore (MultipartFile file) {
		
		String saveDir ="D:\\JavaStudy\\upload";
		
		//오리지날 파일명
		String OrgName = file.getOriginalFilename();
		System.out.println("오리지날 파일명 = "+OrgName);
		
		//확장자
		String exname =file.getOriginalFilename().substring(file.getOriginalFilename().lastIndexOf(".")); //확장자는 점으로 구별하니까 
		System.out.println("확장자 = "+exname);
		
		//저장파일명
		String saveName = System.currentTimeMillis()+UUID.randomUUID().toString() + exname; //지금시간과 랜덤수 +확장자                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
		System.out.println("저장파일명 = "+saveName );
		
		//파일패스//D-javastudy-upload라는 폴더에 저장할 것 이다.
		String filepath = saveDir+"\\"+saveName;
		System.out.println("파일패스 = "+filepath);
		
		//파일사이즈
		long fileSize = file.getSize();
		System.out.println("파일사이즈 = "+ fileSize);
		
		FileVO filevo = new FileVO(filepath, OrgName, saveName, fileSize);
		System.out.println(filevo.toString());
		
		//다오 연결DB저장**
		
		 int no= dao.insert(filevo);
		 System.out.println(no+"등록완료");
		
		//파일 서버에 복사//하드에 복사 
		try {
			byte[] fileData = file.getBytes();
			OutputStream out = new FileOutputStream(saveDir +"/"+ saveName);
			BufferedOutputStream bout = new BufferedOutputStream(out);
			
			bout.write(fileData);
			
			if(bout != null) {
				bout.close();
			}
			
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
		//return filevo;  //사용자에게 보여주려고 응답을 리턴해준다.
		
	
	}
	
	public List<FileVO> list(){
		
		return dao.selectList();
	}
}
